xx = np.array([x])
yy = np.array([y])

input = torch.from_numpy(xx)
target = torch.from_numpy(yy)

class RNN(nn.Module):
    def __init__(self, input_size, output_size, hidden_dim):
        super(RNN, self).__init__()

        # Defining some parameters
        self.hidden_dim = hidden_dim

        #Defining the layers
        self.i2h = nn.Linear(input_size + hidden_dim, hidden_dim)
        self.i2o = nn.Linear(input_size + hidden_dim, output_size)
        self.sigmoid = nn.Sigmoid()
        
    
    def forward(self, input_tensor, hidden_tensor):
        combined = torch.cat((input_tensor, hidden_tensor), 1)

        hidden = self.i2h(combined)
        out = self.i2o(combined)
        out = self.sigmoid(out)
        
        return out, hidden
    
    def init_hidden(self):
        # This method generates the first hidden state of zeros which we'll use in the forward pass
        # We'll send the tensor holding the hidden state to the device we specified earlier as well
        hidden = torch.zeros(1, self.hidden_dim)
        return hidden

# Instantiate the model with hyperparameters
rnn = RNN(input_size=len(x), output_size=len(x), hidden_dim=256)

